    @cImport directly imports types, variables, functions, and simple macros for use in Zig. It even translates inline functions from C into Zig.
      const c =  @cImport( @cInclude( &#34;soundio/soundio.h&#34;)); const std =  @import( &#34;std&#34;); fn  sio_err(err:  c_int) ! void {  switch ( @intToEnum(c.SoundIoError, err)) { .None =&gt; {}, .NoMem =&gt;  return  error.NoMem, .InitAudioBackend =&gt;  return  error.InitAudioBackend, .SystemResources =&gt;  return  error.SystemResources, .OpeningDevice =&gt;  return  error.OpeningDevice, .NoSuchDevice =&gt;  return  error.NoSuchDevice, .Invalid =&gt;  return  error.Invalid, .BackendUnavailable =&gt;  return  error.BackendUnavailable, .Streaming =&gt;  return  error.Streaming, .IncompatibleDevice =&gt;  return  error.IncompatibleDevice, .NoSuchClient =&gt;  return  error.NoSuchClient, .IncompatibleBackend =&gt;  return  error.IncompatibleBackend, .BackendDisconnected =&gt;  return  error.BackendDisconnected, .Interrupted =&gt;  return  error.Interrupted, .Underflow =&gt;  return  error.Underflow, .EncodingString =&gt;  return  error.EncodingString, }} var seconds_offset:  f32 =  0; fn  write_callback( maybe_outstream: ?[*]c.SoundIoOutStream, frame_count_min:  c_int, frame_count_max:  c_int,)  callconv(.C)  void {  const outstream =  @ptrCast(*c.SoundIoOutStream, maybe_outstream);  const layout = &amp;outstream.layout;  const float_sample_rate = outstream.sample_rate;  const seconds_per_frame =  1.0 /  @intToFloat( f32, float_sample_rate);  var frames_left = frame_count_max;  while (frames_left &gt;  0) {  var frame_count = frames_left;  var areas: [*]c.SoundIoChannelArea =  undefined; sio_err(c.soundio_outstream_begin_write( maybe_outstream,  @ptrCast([*]?[*]c.SoundIoChannelArea, &amp;areas), &amp;frame_count, ))  catch |err| std.debug.panic( &#34;write failed: {}&#34;,  @errorName(err));  if (frame_count ==  0)  break;  const pitch =  440.0;  const radians_per_second = pitch *  2.0 * std.math.pi;  var frame:  c_int =  0;  while (frame &lt; frame_count) : (frame +=  1) {  const sample = std.math.sin((seconds_offset +  @intToFloat( f32, frame) * seconds_per_frame) * radians_per_second); {  var channel:  usize =  0;  while (channel &lt;  @intCast( usize, layout.channel_count)) : (channel +=  1) {  const channel_ptr = areas[channel].ptr;  const sample_ptr = &amp;channel_ptr[ @intCast( usize, areas[channel].step * frame)];  @ptrCast(* f32,  @alignCast( @alignOf( f32), sample_ptr)).* = sample; } } } seconds_offset += seconds_per_frame *  @intToFloat( f32, frame_count); sio_err(c.soundio_outstream_end_write(maybe_outstream))  catch |err| std.debug.panic( &#34;end write failed: {}&#34;,  @errorName(err)); frames_left -= frame_count; }} pub  fn  main() ! void {  const soundio = c.soundio_create();  defer c.soundio_destroy(soundio);  try sio_err(c.soundio_connect(soundio)); c.soundio_flush_events(soundio);  const default_output_index = c.soundio_default_output_device_index(soundio);  if (default_output_index &lt;  0)  return  error.NoOutputDeviceFound;  const device = c.soundio_get_output_device(soundio, default_output_index)  orelse  return  error.OutOfMemory;  defer c.soundio_device_unref(device); std.debug.print( &#34;Output device: {s}\n&#34;, .{([*] const  u8)(device.*.name)});  const outstream = c.soundio_outstream_create(device)  orelse  return  error.OutOfMemory;  defer c.soundio_outstream_destroy(outstream); outstream.*.format = c.SoundIoFormatFloat32NE; outstream.*.write_callback = write_callback;  try sio_err(c.soundio_outstream_open(outstream));  try sio_err(c.soundio_outstream_start(outstream));  while ( true) c.soundio_wait_events(soundio);}
